mapper.select:
SELECT v.country,v.offer_id,v.offer_name,r.name_alias AS offer_name_alias,v.traffic_source_id,v.traffic_source_name,/*d.subscription_rate as subscription_rate, */COUNT(v.id) AS convers_num FROM t_voluum_notify v
LEFT JOIN t_config_resources r ON v.offer_id = r.voluum_offer_id
/*LEFT JOIN t_distribution_rate d ON v.`offer_id` = d.voluum_offer_id AND v.traffic_source_id = d.voluum_trafficsource_id*/
WHERE v.create_date BETWEEN #{startDate,jdbcType=VARCHAR} AND #{endDate,jdbcType=VARCHAR}
GROUP BY v.`offer_id`,v.`traffic_source_id`,v.country
