mapper.select:
select count(distinct`date`,firstChannelId,SecChannelId,resourcesName,country) from t_notify_channel_analysis 
where 1 = 1

	and `date` >= #{startDate} 


	and `date` <= #{endDate} 


	 and firstChannelId = #{fristCode} 


	and SecChannelId = #{twoCode} 


	and resourcesName = #{resourcesName} 


	and country = #{country}

SELECT a.`date`,a.firstChannelId,a.SecChannelId,a.resourcesName,c.country,SUM(a.`count`) `count`,b.subscribeCount  
FROM t_notify_channel_analysis a 
LEFT JOIN t_base_mcc c ON a.country = c.iso 
LEFT JOIN (
	SELECT `date`,firstChannelId,SecChannelId,resourcesName,country ,SUM(`count`)subscribeCount 
	FROM t_subscribe_success_analysis 
	where 1 = 1 

		and `date` >= #{startDate} 


		and `date` <= #{endDate} 


	 	and firstChannelId = #{fristCode} 


		and SecChannelId = #{twoCode} 


		and resourcesName = #{resourcesName} 


		and country = #{country} 

	GROUP BY `date`,firstChannelId,SecChannelId,resourcesName,country  
)b ON a.`date` = b.`date` AND a.firstChannelId = b.firstChannelId AND a.SecChannelId = b.SecChannelId
AND a.resourcesName = b.resourcesName AND a.country = b.country  
where 1 = 1

	and a.`date` >= #{startDate} 


	and a.`date` <= #{endDate} 


	 and a.firstChannelId = #{fristCode} 


	and a.SecChannelId = #{twoCode} 


	and a.resourcesName = #{resourcesName} 


	and a.country = #{country} 

GROUP BY a.`date`,a.firstChannelId,a.SecChannelId,a.resourcesName,a.country 
order by a.`date` desc 
limit ${startIndex}, ${pageSize}

